import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import ExpansionPanel from '@material-ui/core/ExpansionPanel';
import ExpansionPanelSummary from '@material-ui/core/ExpansionPanelSummary';
import ExpansionPanelDetails from '@material-ui/core/ExpansionPanelDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';

const useStyles = makeStyles((theme) => ({
	root: {
		width: '100%'
	},
	heading: {
		fontSize: theme.typography.pxToRem(15),
		fontWeight: theme.typography.fontWeightRegular
	}
}));

const TournamentsList = (props) => {
	const classes = useStyles();

	const [expanded, setExpanded] = React.useState('panel1');

	const handleChange = (panel) => (event, newExpanded) => {
		setExpanded(newExpanded ? panel : false);
	};

	const tournamentList = props.tournamentState.tournaments.map((tournament) => {
		return (
			<ExpansionPanel 
                key={tournament.id} 
                expanded={expanded === 'panel1'} onChange={handleChange(tournament.name)}>
				<ExpansionPanelSummary expandIcon={<ExpandMoreIcon />} aria-controls='panel1a-content' id='panel1a-header'>
					<Typography className={classes.heading}>{tournament.name}</Typography>
				</ExpansionPanelSummary>
				<ExpansionPanelDetails>
					<Typography>Details</Typography>
				</ExpansionPanelDetails>
			</ExpansionPanel>
		);
	});

	return <div className={classes.root}>{tournamentList}</div>;
};

export default TournamentsList;
