import React, { Component } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import InputBase from '@material-ui/core/InputBase';
import Divider from '@material-ui/core/Divider';
import SearchIcon from '@material-ui/icons/Search';
import Grid from '@material-ui/core/Grid';
import Chip from '@material-ui/core/Chip';
import ExpandMore from '@material-ui/icons/ExpandMore';
import Avatar from '@material-ui/core/Avatar';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

const searchBarStyles = makeStyles({
	root: {
		padding: '2px 4px',
		display: 'flex',
		alignItems: 'center'
	},
	input: {
		marginLeft: 8,
		flex: 1
	},
	divider: {
		width: 1,
		height: 28,
		margin: 4
	}
});

const chipStyles = makeStyles((theme) => ({
	chip: {
		margin: theme.spacing(1)
	}
}));

const gridStyles = {
	paddingTop: '50px'
};

const searchIconStyles = {
	padding: '10px'
};

const filterMapping = [
	{ label: 'Series', value: 'series' },
	{ label: 'Start Date', value: 'date_start' },
	{ label: 'End Date', value: 'date_end' }
];

const searchBarClasses = searchBarStyles();

class SearchBar extends Component {
	constructor(props) {
		super(props);

		this.searchBarClasses = searchBarStyles();
		const chipClasses = chipStyles();
		this.anchorEl = null;
	}

	handleClose = () => {
		this.anchorEl = null;
	};

	handleClickListItem = (event) => {
		this.anchorEl = event.currentTarget;
	};

	handleMenuItemClick = (event, value) => {
		this.setState({
			filterBy: value
		});
	};

	menuItemList = filterMapping.map((item, index) => {
		return (
			<MenuItem key={index} onClick={(event) => this.handleMenuItemClick(event, item.label)}>
				{item.label}
			</MenuItem>
		);
	});

	render() {
		return (
			<Grid container style={gridStyles}>
				<Grid item md={2} xs={1} />
				<Grid item md={8} xs={10}>
					<Paper className={this.searchBarClasses.root}>
						<Chip
							avatar={
								<Avatar>
									<ExpandMore />
								</Avatar>
							}
							label={this.state.filterBy}
							clickable
							className={this.chipClasses.chip}
							color='primary'
							onClick={this.handleClickListItem}
						/>
						<Menu id='simple-menu' anchorEl={anchorEl} open={Boolean(this.anchorEl)} onClose={handleClose}>
							{this.menuItemList}
						</Menu>
						<Divider className={this.searchBarClasses.divider} />
						<InputBase className={this.searchBarClasses.input} placeholder='Search' />
						<SearchIcon style={searchIconStyles} />
					</Paper>
				</Grid>
				<Grid item md={2} xs={1} />
			</Grid>
		);
	}
}

export default SearchBar;
