import React, { Component } from 'react';
import Auxiliary from '../../hoc/Auxiliary';
import axios from 'axios';
import jsonServerConfig from '../../configurations/json-server';

class TournamentsView extends Component {
	constructor(props) {
		super(props);

		const { host, port } = jsonServerConfig;
		this.url = `http://${host}:${port}`;
		this.tournamentsUrl = `${this.url}/tournaments`;

		this.state = {
			tournaments: [],
			isError: false
		};
	}

	async getAllTournaments() {
		axios
			.get(this.tournamentsUrl)
			.then((res) => {
				return Promise.resolve(res.data);
			})
			.catch((err) => {
				return Promise.reject(err);
			});
	}

	render() {
		return (
			<Auxiliary>
				<div>Tournaments List</div>
			</Auxiliary>
		);
	}
}

export default WithTournamentsData('http://localhost:3005/tournaments')(TournamentsView);
