import React, { Component } from 'react';
import Auxiliary from '../../hoc/Auxiliary';
import TournamentService from '../../services/tournamentService';
import TournamentsList from '../../components/TournamentsList/TournamentsList';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

class TournamentsView extends Component {
	constructor(props, TournamentService) {
		super(props);

		this.state = {
			tournaments: null,
			isLoaded: false,
			errorMessage: null
		};
	}

	render() {
		let retrievedTournaments = <TournamentsList tournaments={this.state.tournaments} />;

		if (this.state.tournaments) {
			retrievedTournaments = (
				<div>
					<Paper>
						<Typography component='p'>There is no tournaments to load.</Typography>
					</Paper>
				</div>
			);
		}

		if (this.state.errorMessage) {
			retrievedTournaments = (
				<div>
					<Paper>
						<Typography component='p'>{this.state.errorMessage}</Typography>
					</Paper>
				</div>
			);
		}

		return <Auxiliary>{retrievedTournaments}</Auxiliary>;
	}
}

export default TournamentsView;
