import React, { Component } from 'react';
import Auxiliary from '../../hoc/Auxiliary';
import TournamentsList from '../../components/TournamentsList/TournamentsList';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';
import axios from 'axios';
import jsonServerConfig from '../../configurations/json-server';

class TournamentsView extends Component {
	constructor(props) {
		super(props);

		const { host, port } = jsonServerConfig;
		this.url = `http://${host}:${port}`;
		this.tournamentsUrl = `${this.url}/tournaments`;

		
	}

	componentDidMount() {
		console.log(this.state);
		this.getAllTournamentsService();
	}

	getAllTournamentsService = () => {
		axios
			.get(this.tournamentsUrl)
			.then((res) => {
				this.setState({
					tournaments: res.data,
					isLoaded: true
				});
			})
			.catch((err) => {
				this.setState({
					errorMessage: 'Error occurred while loading tournaments.',
					isLoaded: true
				});
			});
	};

	render() {
		let retrievedTournaments = <TournamentsList tournaments={this.state.tournaments} />;

		if (this.state.tournaments) {
			retrievedTournaments = (
				<div>
					<Paper>
						<Typography component='p'>There is no tournaments to load.</Typography>
					</Paper>
				</div>
			);
		}

		if (this.state.errorMessage) {
			retrievedTournaments = (
				<div>
					<Paper>
						<Typography component='p'>{this.state.errorMessage}</Typography>
					</Paper>
				</div>
			);
		}

		return <Auxiliary>{retrievedTournaments}</Auxiliary>;
	}
}

export default TournamentsView;
