import React, { Component } from 'react';
import Auxiliary from '../../hoc/Auxiliary';
import jsonServerConfig from '../../configurations/json-server';

class TournamentsView extends Component {
	constructor(props) {
		super(props);

		const { host, port } = jsonServerConfig;
		this.url = `http://${host}:${port}`;
		this.tournamentsUrl = `${this.url}/tournaments`;

		this.state = {
			tournaments: [],
			isLoading: true,
			errMessage: ''
		};
	}

	getAllTournaments = () => {
		fetch(this.tournamentsUrl)
			.then((res) => res.json())
			.then((tournaments) => {
				this.setState({
					tournaments: tournaments,
					isLoading: false
				});
			})
			.catch((error) => {
				this.setState({
					errMessage: error
				});
			});
	};

	componentDidMount() {
		this.getAllTournaments();
		console.log(this.state.tournaments);
	}

	render() {
		return (
			<Auxiliary>
				<div>Tournaments List</div>
			</Auxiliary>
		);
	}
}

export default TournamentsView;
