import axios from 'axios';
import jsonServerConfig from '../../src/configurations/json-server';

class TournamentService {
	constructor() {
		const { host, port } = jsonServerConfig;
		this.url = `http://${host}:${port}`;
		this.tournamentsUrl = `${this.url}/tournaments`;
	}

	async getAllTournaments() {
		axios.get(tournamentUrl)
			.then((res) => {
				console.log(res);
				return new Promise((resolve, reject) => {
					resolve(res.data);
				});
			})
			.catch((err) => {
				return new Promise((resolve, reject) => {
					reject(err);
				});
			});
	}

	async getTournamentById(tournamentId) {
		const tournamentUrl = `${this.tournamentsUrl}/${tournamentId}`;
		axios
			.get(tournamentUrl)
			.then((res) => {
				return new Promise((resolve, reject) => {
					resolve(res.data);
				});
			})
			.catch((err) => {
				return new Promise((resolve, reject) => {
					reject(err);
				});
			});
	}

	async getFilteredTournaments(filterField, inputValue) {
		const filterUrl = `${this.tournamentsUrl}?series.${filterField}_like=${inputValue}`;
		axios
			.get(filterUrl)
			.then((res) => {
				return new Promise((resolve, reject) => {
					resolve(res.data);
				});
			})
			.catch((err) => {
				return new Promise((resolve, reject) => {
					reject(err);
				});
			});
	}
}

export default TournamentService;
