import React, { Component } from 'react';
import Auxiliary from '../../hoc/Auxiliary';
import TournamentService from '../../services/tournamentService';
import TournamentsList from '../../components/TournamentsList/TournamentsList';
import { makeStyles } from '@material-ui/core/styles';
import Paper from '@material-ui/core/Paper';
import Typography from '@material-ui/core/Typography';

class TournamentsView extends Component {
	constructor(props) {
		super(props);

		const useStyles = makeStyles(theme => ({
			root: {
			  padding: theme.spacing(3, 2),
			},
		  }));

		this.tournamentService = new TournamentService();
		this.state = {
			tournaments: [],
			isLoaded: false,
			errorMessage: ''
		};


	}

	componentDidMount() {
		this.getAllTournaments();
	}

	getAllTournaments() {
		this.tournamentService
			.getAllTournaments()
			.then((tournaments) => {
				this.setState({
					tournaments: tournaments,
					isLoaded: true
				});
			})
			.catch((err) => {
				this.setState({
					errorMessage: 'Error occurred while loading tournaments...'
				});
			});
	}

	render() {
		let retrievedTournaments = <TournamentsList tournaments={this.state.tournaments} />;

		if (this.state.tournaments.length === 0) {
			retrievedTournaments = (
				<div>
					<Paper className={this.classes.root}>
						<Typography component='p'>There is no tournaments to load.</Typography>
					</Paper>
				</div>
			);
		}

		if (this.state.errorMessage.length !== 0) {
			retrievedTournaments = (
				<div>
					<Paper className={this.classes.root}>
						<Typography component='p'>{this.state.errorMessage}</Typography>
					</Paper>
				</div>
			);
		}

		return <Auxiliary>{retrievedTournaments}</Auxiliary>;
	}
}

export default TournamentsView;
