import React, { Component } from 'react';
import Auxiliary from '../../hoc/Auxiliary';
import axios from 'axios';
import jsonServerConfig from '../../configurations/json-server';

class TournamentsView extends Component {
	constructor(props) {
		super(props);

		const { host, port } = jsonServerConfig;
		this.url = `http://${host}:${port}`;
		this.tournamentsUrl = `${this.url}/tournaments`;

		this.state = {
			tournaments: null,
			isError: false
		};
	}

	getAllTournaments() {
		axios
			.get(this.tournamentsUrl)
			.then((res) => {
				console.log(res.data);
				this.setState({
					tournaments: res.data
				});
			})
			.catch((err) => {
				this.setState({
					isError: true
				});
			});
	}

	componentDidMount() {
		this.getAllTournaments();
		// console.log(this.state);
	}

	render() {
		return (
			<Auxiliary>
				<div>Tournaments List</div>
			</Auxiliary>
		);
	}
}

export default TournamentsView;
