import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';

const seriesStyles = makeStyles((theme) => ({
	card: {
		minWidth: 250
	}
}));

const dividerStyles = {
	backgroundColor: '#448ad4',
	margin: '5px 0',
	height: 1.5
};

const SeriesDetails = (props) => {
	const seriesClasses = seriesStyles();

	return (
		<Card className={seriesClasses.card}>
			<CardContent>
				<Typography variant='h6' component='h2'>
					{props.seriesDetails.name}
				</Typography>
				<Divider style={dividerStyles} />
				<Typography variant='body2' component='p'>
				<Grid container spacing={2}>
					<Grid item md={6} xs={12}>
						<TournamentDetails tournamentDetails={props.tournamentDetails} />
					</Grid>
					<Grid item md={6} xs={12}>
						<SeriesDetails seriesDetails={props.tournamentDetails.series} />
					</Grid>
				</Grid>
				</Typography>
			</CardContent>
		</Card>
	);
};

export default SeriesDetails;
