import React, { Component } from 'react';
import axios from 'axios';

const withTournamentsData = (Url) => (WrappedComponent) => {
	return class extends Component {
		constructor(props) {
			super(props);

			this.state = {
				tournaments: [],
				isError: false
			};
		}

		componentDidMount() {
			axios
				.get(Url)
				.then((res) => {
					console.log(res.data);
					this.setState({
						tournaments: res.data
					});
				})
				.catch((err) => {
					this.setState({
						isError: true
					});
				});
		}

		render() {
			return <WrappedComponent {...this.props} {...this.state} />;
		}
	};
};

export default withTournamentsData;
